

function getDataForm(selectForm) {
    let form = document.querySelector(selectForm);
    let submit = form.querySelector('.form__submit');
    function sentForm(e) {
        e.preventDefault();
        let formData = new FormData(form);
        let ajax = new XMLHttpRequest();
        ajax.open('post', 'http://logist-master/api/calculator', false);
        ajax.addEventListener('readystatechange', function () {
            if (this.readyState == 4 && this.status == 200) {
                // console.log(this.responseText);
            }
        });
        ajax.send(formData);
        let res;
        res = JSON.parse(ajax.responseText);
        console.log(res);

        let showData = document.querySelector('.showData');
        showData.innerHTML = '';
        for (key in res) {
            showData.innerHTML += `${key} : ${res[key]} <br>`;
        }
    }
    submit.addEventListener('click', sentForm);
}

getDataForm('.form-calculator');
